import React, { useState, useEffect } from 'react';
import { getEmployees } from '../services/api';
import '../components/Table.css'; // <-- 1. IMPORT THE NEW TABLE CSS

const EmployeeListPage = () => {
    const [employees, setEmployees] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    // ... state for filters and pagination ...

    useEffect(() => {
        setIsLoading(true);
        getEmployees({ page: 0, size: 20 })
            .then(response => {
                setEmployees(response.data.content);
            })
            .catch(err => {
                setError('Failed to load employees.');
            })
            .finally(() => {
                setIsLoading(false);
            });
    }, []);

    if (isLoading) return <p>Loading employees...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div>
            <h2>Employee Management</h2>
            {/* Filters would go here */}

            {/* 2. ADD THE 'app-table' CLASSNAME TO THE TABLE */}
            <table className="app-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Department</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map(emp => (
                        <tr key={emp.id}>
                            <td>{emp.name}</td>
                            <td>{emp.email}</td>
                            <td>{emp.department || 'N/A'}</td>
                            <td>{emp.status || 'N/A'}</td>
                            <td>
                                <button className="action-button" style={{backgroundColor: '#007bff'}}>View</button>
                                <button className="action-button" style={{backgroundColor: '#6c757d', marginLeft: '0.5rem'}}>Edit</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default EmployeeListPage;
