import React from 'react';
import { NavLink } from 'react-router-dom';

const SideBar = () => {
    // Get user info to conditionally render the manager-only link
    const user = JSON.parse(localStorage.getItem('user'));
    const isManager = user?.roles.includes('ROLE_MANAGER');
    
    // This check is for the main Leave Management link, visible to HR and Managers
    const canManageLeave = user?.roles.includes('ROLE_HR') || user?.roles.includes('ROLE_MANAGER');

    return (
        <nav className="sidebar">
            <div className="sidebar-header">
                <h3>PayFlow</h3>
            </div>
            <ul>
                <li><NavLink to="/dashboard/home">Dashboard</NavLink></li>
                <li><NavLink to="/dashboard/employees">Employees</NavLink></li>
                <li><NavLink to="/dashboard/onboard">Onboard New Hire</NavLink></li>
                
                {/* Conditionally show the leave section for HR or Manager */}
                {canManageLeave && (
                    <>
                        <hr style={{borderColor: '#495057'}} />
                        <li><NavLink to="/dashboard/leave">Leave Management</NavLink></li>
                    </>
                )}
                
                {/* Conditionally show the approval link ONLY for Managers */}
                {isManager && (
                    <li><NavLink to="/dashboard/approve-leave">Approve Leaves</NavLink></li>
                )}
            </ul>
        </nav>
    );
};

 export default SideBar;
