import React from 'react';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

const MySwal = withReactContent(Swal);

const TopNavBar = ({ user, onLogout }) => {
    const confirmLogout = () => {
        MySwal.fire({
            title: 'Are you sure?',
            text: "You will be logged out of your session.",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, log out!',
            cancelButtonText: 'No, stay logged in',
            confirmButtonColor: '#3085d6', // Blue for confirm
            cancelButtonColor: '#d33',   // Red for cancel
            // We can use Bootstrap's classes to style the buttons inside the pop-up
            customClass: {
                confirmButton: 'btn btn-primary',
                cancelButton: 'btn btn-secondary ml-2' // ml-2 adds a little space
            },
            buttonsStyling: false // This is important to let our custom classes work
        }).then((result) => {
            if (result.isConfirmed) {
                onLogout();
            }
        });
    };

    return (
        <div className="top-navbar">
            <span>Welcome, <strong>{user?.username || 'User'}</strong></span>
            
            {/* --- THIS IS THE FIX --- */}
            {/* We will use Bootstrap's classes directly. */}
            {/* 'btn-sm' makes it smaller. 'btn-danger' makes it red. 'ml-3' adds margin. */}
            <button onClick={confirmLogout} className="btn btn-danger btn-sm ml-3">
                Logout
            </button>
        </div>
    );
};

export default TopNavBar;
