import React, {useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import { getPendingLeaveRequests, updateLeaveRequestStatus } from '../services/api';
import '../components/Table.css';

const MySwal = withReactContent(Swal);

const LeaveApprovalPage = () => {
    const [pendingRequests, setPendingRequests] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null); // <-- Use state to display the error

    const fetchPendingRequests = () => {
        setIsLoading(true);
        setError(null); // Reset error on new fetch
        getPendingLeaveRequests()
            .then(res => {
                setPendingRequests(res.data);
            })
            .catch(err => {
                // --- THIS IS THE MOST IMPORTANT PART ---
                console.error("!!! CRITICAL API ERROR !!!", err); // Log the full error object
                let detailedError = "An unknown error occurred.";
                if (err.response) {
                    // Error came from the server (e.g., 403, 404, 500)
                    detailedError = `Error: ${err.response.status} - ${err.response.statusText}. `;
                    if (typeof err.response.data === 'string') {
                        detailedError += `Message: ${err.response.data}`;
                    } else {
                        detailedError += `Message: ${JSON.stringify(err.response.data)}`;
                    }
                } else if (err.request) {
                    // Request was made but no response was received (e.g., server is down)
                    detailedError = "The server is not responding. Please ensure the backend is running.";
                } else {
                    // Something happened in setting up the request
                    detailedError = `Request setup error: ${err.message}`;
                }
                setError(detailedError); // <-- Set the error state to display it
            })
            .finally(() => {
                setIsLoading(false);
            });
    };

    useEffect(() => { fetchPendingRequests(); }, []);

    const handleAction = (requestId, newStatus) => { /* ... same as before ... */ };
    
    // --- RENDER LOGIC ---
    if (isLoading) {
        return <p>Loading pending requests...</p>;
    }

    // If there was an error, display it clearly
    if (error) {
        return (
            <div>
                <h2>Approve Leave Requests</h2>
                <div className="error-message" style={{backgroundColor: '#f8d7da', border: '1px solid #f5c6cb', padding: '1rem', borderRadius: '5px'}}>
                    <h4 style={{color: '#721c24'}}>Failed to Load Data</h4>
                    <p>The following error occurred while trying to fetch pending requests:</p>
                    <pre style={{whiteSpace: 'pre-wrap', wordBreak: 'break-all'}}>{error}</pre>
                    <button className="btn btn-primary btn-sm" onClick={fetchPendingRequests}>Try Again</button>
                </div>
            </div>
        );
    }

    return (
        <div>
            <h2>Approve Leave Requests</h2>
            <p>Review and act upon pending leave requests from all employees.</p>
            <table className="app-table">
                {/* ... table content remains the same ... */}
            </table>
        </div>
    );
};
export default LeaveApprovalPage;
