import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { getManageableUsers, updateUserStatus, createUser } from '../services/api';
import '../components/Modal.css';
import '../components/Table.css'; // <-- 1. IMPORT THE SHARED TABLE CSS

Modal.setAppElement('#root');

const ManageUsersPage = () => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);

    const [newUser, setNewUser] = useState({ username: '', email: '', password: '', role: 'ROLE_HR' });
    const [formError, setFormError] = useState('');
    const [formSuccess, setFormSuccess] = useState('');

    const fetchUsers = () => {
        setIsLoading(true);
        getManageableUsers()
            .then(response => setUsers(response.data))
            .catch(err => setError('Failed to load users.'))
            .finally(() => setIsLoading(false));
    };

    useEffect(() => { fetchUsers(); }, []);

    const handleStatusToggle = (userId, currentStatus) => {
        updateUserStatus(userId, !currentStatus)
            .then(() => fetchUsers())
            .catch(err => alert('Failed to update user status.'));
    };

    const openModal = () => setIsModalOpen(true);
    const closeModal = () => {
        setIsModalOpen(false);
        setFormError('');
        setFormSuccess('');
        setNewUser({ username: '', email: '', password: '', role: 'ROLE_HR' });
    };

    const handleNewUserChange = (e) => {
        const { name, value } = e.target;
        setNewUser(prev => ({ ...prev, [name]: value }));
    };

    const handleCreateUserSubmit = (e) => {
        e.preventDefault();
        setFormError('');
        setFormSuccess('');
        createUser(newUser.username, newUser.password, newUser.role, newUser.email)
            .then(() => {
                setFormSuccess(`User '${newUser.username}' created successfully!`);
                fetchUsers();
                setTimeout(() => {
                    closeModal();
                }, 2000);
            })
            .catch(err => setFormError(err.response?.data || 'Failed to create user.'));
    };

    if (isLoading) return <p>Loading users...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h2>Manage Users (HR & Managers)</h2>
                <button className="btn" onClick={openModal}>+ Create New User</button>
            </div>

            {/* 2. ADD THE 'app-table' CLASSNAME TO THE TABLE ELEMENT */}
            <table className="app-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.username}</td>
                            <td>{user.email || 'N/A'}</td>
                            <td>{user.role.replace('ROLE_', '')}</td>
                            <td>
                                <span style={{
                                    fontWeight: 'bold',
                                    color: user.enabled ? '#28a745' : '#dc3545'
                                }}>
                                    {user.enabled ? 'Active' : 'Disabled'}
                                </span>
                            </td>
                            <td>
                                {/* 3. APPLY THE SHARED ACTION BUTTON CLASSES */}
                                <button
                                    onClick={() => handleStatusToggle(user.id, user.enabled)}
                                    className={`action-button ${user.enabled ? 'btn-disable' : 'btn-enable'}`}
                                >
                                    {user.enabled ? 'Disable' : 'Enable'}
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* The Modal for creating a new user */}
            <Modal isOpen={isModalOpen} onRequestClose={closeModal} className="modal-content" overlayClassName="modal-overlay">
                <h2>Create New User</h2>
                <button onClick={closeModal} className="modal-close-button">×</button>
                {formError && <p className="error-message">{formError}</p>}
                {formSuccess && <p className="success-message">{formSuccess}</p>}
                <form onSubmit={handleCreateUserSubmit}>
                    <div className="form-group"><label>Username</label><input type="text" name="username" value={newUser.username} onChange={handleNewUserChange} required /></div>
                    <div className="form-group"><label>Email</label><input type="email" name="email" value={newUser.email} onChange={handleNewUserChange} required /></div>
                    <div className="form-group"><label>Temporary Password</label><input type="password" name="password" value={newUser.password} onChange={handleNewUserChange} required /></div>
                    <div className="form-group"><label>Role</label><select name="role" value={newUser.role} onChange={handleNewUserChange}><option value="ROLE_HR">HR</option><option value="ROLE_MANAGER">Manager</option></select></div>
                    <button type="submit" className="btn">Submit Creation</button>
                </form>
            </Modal>
        </div>
    );
};

export default ManageUsersPage;
