import axios from 'axios';

// ... existing axios instance setup and interceptor ...
const api = axios.create({
    baseURL: 'http://localhost:8080/api',
    headers: { 'Content-Type': 'application/json' },
});
api.interceptors.request.use(/* ... */);


// ... existing login, resetPassword, createUser functions ...

// --- HR DASHBOARD SERVICE ---
export const getDashboardStats = () => {
    return api.get('/hr/dashboard/stats');
};

export const getEmployees = (params) => {
    // params will be an object like { page: 0, size: 10, department: 'Engineering' }
    return api.get('/hr/employees', { params });
};

// ... existing onboardEmployee (may need updates) ...

export default api;
