import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { getManageableUsers, updateUserStatus, createUser } from '../services/api';

// Import all necessary stylesheets
import '../components/Modal.css';
import '../components/Table.css';

// Set the app element for the modal to avoid accessibility warnings
Modal.setAppElement('#root');

const ManageUsersPage = () => {
    // State for the main user list
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    // State for the "Create User" modal
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [newUser, setNewUser] = useState({
        username: '',
        email: '',
        password: '',
        role: 'ROLE_HR'
    });
    const [formError, setFormError] = useState('');
    const [formSuccess, setFormSuccess] = useState('');

    // --- Data Fetching ---
    const fetchUsers = () => {
        setIsLoading(true);
        getManageableUsers()
            .then(response => {
                setUsers(response.data);
            })
            .catch(err => {
                setError('Failed to load users. Please try again later.');
                console.error(err);
            })
            .finally(() => {
                setIsLoading(false);
            });
    };

    // Fetch users when the component first mounts
    useEffect(() => {
        fetchUsers();
    }, []);

    // --- Event Handlers ---
    const handleStatusToggle = (userId, currentStatus) => {
        updateUserStatus(userId, !currentStatus)
            .then(() => {
                // Refresh the list to show the change immediately
                fetchUsers();
            })
            .catch(err => {
                alert('Failed to update user status.');
                console.error(err);
            });
    };

    const openModal = () => setIsModalOpen(true);
    const closeModal = () => {
        setIsModalOpen(false);
        // Reset form state when closing the modal
        setFormError('');
        setFormSuccess('');
        setNewUser({ username: '', email: '', password: '', role: 'ROLE_HR' });
    };

    const handleNewUserChange = (e) => {
        const { name, value } = e.target;
        setNewUser(prev => ({ ...prev, [name]: value }));
    };

    const handleCreateUserSubmit = (e) => {
        e.preventDefault();
        setFormError('');
        setFormSuccess('');

        createUser(newUser.username, newUser.password, newUser.role, newUser.email)
            .then(() => {
                setFormSuccess(`User '${newUser.username}' created successfully!`);
                fetchUsers(); // Refresh the list
                // Close the modal automatically after a short delay
                setTimeout(() => {
                    closeModal();
                }, 2000);
            })
            .catch(err => {
                setFormError(err.response?.data || 'Failed to create user.');
            });
    };

    if (isLoading) return <p>Loading users...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div>
            {/* --- Perfectly Aligned Page Header --- */}
            <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                paddingBottom: '1rem',
                borderBottom: '1px solid #dee2e6'
            }}>
                <h2 style={{ margin: 0, fontSize: '1.75rem' }}></h2>
                <button
                    onClick={openModal}
                    className="btn btn-header" // Using the smaller button style
                >
                    + Create New User
                </button>
            </div>

            {/* --- Professionally Styled User Table --- */}
            <table className="app-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.username}</td>
                            <td>{user.email || 'N/A'}</td>
                            <td>{user.role.replace('ROLE_', '')}</td>
                            <td>
                                <span style={{ fontWeight: 'bold', color: user.enabled ? '#28a745' : '#dc3545' }}>
                                    {user.enabled ? 'Active' : 'Disabled'}
                                </span>
                            </td>
                            <td>
                                <button
                                    onClick={() => handleStatusToggle(user.id, user.enabled)}
                                    className={`action-button ${user.enabled ? 'btn-disable' : 'btn-enable'}`}
                                >
                                    {user.enabled ? 'Disable' : 'Enable'}
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* --- Create User Modal --- */}
            <Modal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
                className="modal-content"
                overlayClassName="modal-overlay"
            >
                <h2>Create New User</h2>
                <button onClick={closeModal} className="modal-close-button">Ã—</button>
                {formError && <p className="error-message">{formError}</p>}
                {formSuccess && <p className="success-message">{formSuccess}</p>}
                <form onSubmit={handleCreateUserSubmit}>
                    <div className="form-group"><label>Username</label><input type="text" name="username" value={newUser.username} onChange={handleNewUserChange} required /></div>
                    <div className="form-group"><label>Email</label><input type="email" name="email" value={newUser.email} onChange={handleNewUserChange} required /></div>
                    <div className="form-group"><label>Temporary Password</label><input type="password" name="password" value={newUser.password} onChange={handleNewUserChange} required minLength="6" /></div>
                    <div className="form-group"><label>Role</label><select name="role" value={newUser.role} onChange={handleNewUserChange}><option value="ROLE_HR">HR</option><option value="ROLE_MANAGER">Manager</option></select></div>
                    <button type="submit" className="btn">Submit Creation</button>
                </form>
            </Modal>
        </div>
    );
};

export default ManageUsersPage;
