import React, { useState, useEffect } from 'react';
import { Navigate, Route, Routes, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import 'bootstrap/dist/css/bootstrap.min.css';

// --- Import all Layouts and Pages ---
import LoginPage from './pages/LoginPage';
import ResetPasswordPage from './pages/ResetPasswordPage';
import AdminLayout from './layouts/AdminLayout';
import DashboardLayout from './layouts/DashboardLayout';
import AdminHomePage from './pages/AdminHomePage';
import ManageUsersPage from './pages/ManageUsersPage';
import DashboardHomePage from './pages/DashboardHomePage';
import EmployeeListPage from './pages/EmployeeListPage';
import OnboardingPage from './pages/OnboardingPage';
import LeaveManagementPage from './pages/LeaveManagementPage';
import LeaveApprovalPage from './pages/LeaveApprovalPage';
import './App.css';

const MySwal = withReactContent(Swal);

/**
 * A wrapper component to protect routes that require authentication and role-based access.
 * It checks for a valid user session, correct roles, and mandatory password reset.
 */
const ProtectedRoute = ({ children, allowedRoles }) => {
    const user = JSON.parse(localStorage.getItem('user'));

    // 1. Check if user is logged in
    if (!user) {
        return <Navigate to="/login" />;
    }

    // 2. Check if the user's role is permitted for this route
    const hasRole = user.roles.some(role => allowedRoles.includes(role));
    if (!hasRole) {
        // Redirect if the role is not authorized. This is a crucial security layer.
        return <Navigate to="/login" />;
    }

    // 3. Check for mandatory password reset
    if (user.firstTimeLogin) {
        return <Navigate to="/reset-password" />;
    }

    // 4. If all checks pass, render the requested page
    return children;
};


function App() {
    const [, setUser] = useState(null);
    const navigate = useNavigate();

    // On initial app load, check localStorage for a persisted user session.
    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    /**
     * Handles successful login by saving user data and navigating to the appropriate dashboard.
     */
    const handleLoginSuccess = (userData) => {
        localStorage.setItem('user', JSON.stringify(userData));
        setUser(userData);

        if (userData.firstTimeLogin) {
            navigate('/reset-password');
            return;
        }

        if (userData.roles.includes('ROLE_ADMIN')) {
            navigate('/admin/dashboard');
        } else if (userData.roles.includes('ROLE_HR') || userData.roles.includes('ROLE_MANAGER')) {
            navigate('/dashboard/home');
        } else {
            navigate('/login');
        }
    };

    /**
     * Handles user logout by clearing the session and showing a confirmation pop-up.
     */
    const handleLogout = () => {
        localStorage.removeItem('user');
        setUser(null);
        MySwal.fire({
            icon: 'success',
            title: 'Logged Out',
            text: 'You have been successfully logged out.',
            timer: 1500,
            showConfirmButton: false,
        });
        navigate('/login');
    };

    return (
        <Routes>
            {/* --- PUBLIC ROUTES (Accessible to everyone) --- */}
            <Route path="/login" element={<LoginPage onLoginSuccess={handleLoginSuccess} />} />
            <Route path="/reset-password" element={<ResetPasswordPage onPasswordResetSuccess={handleLogout} />} />

            {/* --- ADMIN DASHBOARD ROUTES --- */}
            <Route
                path="/admin/*"
                element={
                    <ProtectedRoute allowedRoles={['ROLE_ADMIN']}>
                        <AdminLayout />
                    </ProtectedRoute>
                }
            >
                <Route path="dashboard" element={<AdminHomePage />} />
                <Route path="manage-users" element={<ManageUsersPage />} />
                <Route index element={<Navigate to="dashboard" />} />
            </Route>

            {/* --- UNIFIED HR & MANAGER DASHBOARD ROUTES --- */}
            <Route
                path="/dashboard/*"
                element={
                    <ProtectedRoute allowedRoles={['ROLE_HR', 'ROLE_MANAGER']}>
                        <DashboardLayout />
                    </ProtectedRoute>
                }
            >
                {/* Routes accessible to BOTH HR and Manager */}
                <Route path="home" element={<DashboardHomePage />} />
                <Route path="employees" element={<EmployeeListPage />} />
                <Route path="onboard" element={<OnboardingPage />} />

                {/* --- MANAGER-EXCLUSIVE LEAVE ROUTES --- */}
                {/* A nested protected route ONLY for Managers. HR users will be redirected. */}
                <Route 
                    path="leave" 
                    element={
                        <ProtectedRoute allowedRoles={['ROLE_MANAGER']}>
                            <LeaveManagementPage />
                        </ProtectedRoute>
                    } 
                />
                // in App.js

    {/* ... Admin and HR/Manager routes ... */}

    {/* +++ NEW EMPLOYEE DASHBOARD ROUTES +++ */}
    <Route
        path="/employee/*"
        element={
            <ProtectedRoute allowedRoles={['ROLE_EMPLOYEE']}>
                <EmployeeDashboardLayout /> 
            </ProtectedRoute>
        }
    >
        <Route path="dashboard" element={<EmployeeHomePage />} />
        <Route path="leave" element={<LeaveManagementPage />} />
        <Route index element={<Navigate to="dashboard" />} />
    </Route>


                {/* This route is also protected ONLY for Managers */}
                <Route 
                    path="approve-leave" 
                    element={
                        <ProtectedRoute allowedRoles={['ROLE_MANAGER']}>
                            <LeaveApprovalPage />
                        </ProtectedRoute>
                    } 
                />

                {/* Default route for the dashboard */}
                <Route index element={<Navigate to="home" />} />
            </Route>

            {/* --- DEFAULT CATCH-ALL ROUTE --- */}
            <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
    );
}

export default App;
