import React from 'react';
import { NavLink } from 'react-router-dom';

const SideBar = () => {
    // This check is now used for the entire leave section
    const user = JSON.parse(localStorage.getItem('user'));
    const isManager = user?.roles.includes('ROLE_MANAGER');

    return (
        <nav className="sidebar">
            <div className="sidebar-header">
                <h3>PayFlow</h3>
            </div>
            <ul>
                <li><NavLink to="/dashboard/home">Dashboard</NavLink></li>
                <li><NavLink to="/dashboard/employees">Employees</NavLink></li>
                <li><NavLink to="/dashboard/onboard">Onboard New Hire</NavLink></li>
                
                {/* --- THIS ENTIRE BLOCK IS NOW CONDITIONAL --- */}
                {/* If the user is a manager, show the separator and the leave links. */}
                {/* Otherwise, show nothing. */}
                {isManager && (
                    <>
                        <hr style={{borderColor: '#495057'}} />
                        <li><NavLink to="/dashboard/leave">Leave Management</NavLink></li>
                        <li><NavLink to="/dashboard/approve-leave">Approve Leaves</NavLink></li>
                    </>
                )}
            </ul>
        </nav>
    );
};

export default SideBar;
