import React, { useState, useEffect } from 'react';
import { getManageableUsers, updateUserStatus } from '../services/api';

const ManageUsersPage = () => {
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    const fetchUsers = () => {
        setIsLoading(true);
        getManageableUsers()
            .then(response => setUsers(response.data))
            .catch(err => setError('Failed to load users.'))
            .finally(() => setIsLoading(false));
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    const handleStatusToggle = (userId, currentStatus) => {
        const newStatus = !currentStatus;
        updateUserStatus(userId, newStatus)
            .then(() => {
                // Refresh the user list to show the change
                fetchUsers();
            })
            .catch(err => alert('Failed to update user status.'));
    };

    if (isLoading) return <p>Loading users...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div>
            <h2>Manage Users (HR & Managers)</h2>
            {/* Add 'Create New User' form/button here later */}
            <table className="table" style={{width: '100%', marginTop: '1.5rem'}}>
                <thead>
                    <tr>
                        <th>Name</th><th>Email</th><th>Role</th><th>Status</th><th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.username}</td>
                            <td>{user.email || 'N/A'}</td>
                            <td>{user.role.replace('ROLE_', '')}</td>
                            <td>{user.enabled ? 'Active' : 'Disabled'}</td>
                            <td>
                                <button
                                    onClick={() => handleStatusToggle(user.id, user.enabled)}
                                    className={user.enabled ? 'logout-btn' : 'btn'}
                                    style={user.enabled ? {backgroundColor: '#dc3545'} : {backgroundColor: '#28a745'}}
                                >
                                    {user.enabled ? 'Disable' : 'Enable'}
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ManageUsersPage;
