import React, { useState, useEffect } from 'react';
import { getAdminDashboardStats } from '../services/api';
import Card from '../components/Card';
import ClockWidget from '../components/ClockWidget';

const AdminHomePage = () => {
    const [stats, setStats] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        getAdminDashboardStats()
            .then(response => setStats(response.data))
            .catch(err => setError('Failed to load dashboard data.'));
    }, []);

    const currentDate = new Date().toLocaleDateString('en-US', {
        weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'
    });

    if (error) return <p className="error-message">{error}</p>;
    if (!stats) return <p>Loading dashboard...</p>;

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h1>Admin Dashboard</h1>
                <ClockWidget />
            </div>
            <p style={{marginTop: 0}}>Today is {currentDate}.</p>
            
            <div style={{ display: 'flex', gap: '1.5rem', flexWrap: 'wrap', marginTop: '2rem' }}>
                <Card title="Total Active Users" value={stats.totalActiveUsers} />
                <Card title="Total HRs" value={stats.totalHrs} />
                <Card title="Total Managers" value={stats.totalManagers} />
                <Card title="Disabled Users" value={stats.disabledUsers} />
            </div>
        </div>
    );
};

export default AdminHomePage;
