import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { getEmployees, updateEmployee } from '../services/api';
import '../components/Table.css'; // Reuse our professional table styles
import '../components/Modal.css'; // Reuse our modal styles

Modal.setAppElement('#root');

const EmployeeListPage = () => {
    const [employees, setEmployees] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    // --- State for the Edit Modal ---
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    const [formError, setFormError] = useState('');

    const fetchEmployees = () => {
        setIsLoading(true);
        getEmployees({ page: 0, size: 20 })
            .then(response => setEmployees(response.data.content))
            .catch(err => setError('Failed to load employees.'))
            .finally(() => setIsLoading(false));
    };

    useEffect(() => { fetchEmployees(); }, []);

    // --- Modal Handler Functions ---
    const openEditModal = (employee) => {
        // The date fields need to be in YYYY-MM-DD format for the input[type=date]
        const formattedEmployee = {
            ...employee,
            dateJoined: employee.dateJoined ? new Date(employee.dateJoined).toISOString().split('T')[0] : '',
            dateOfBirth: employee.dateOfBirth ? new Date(employee.dateOfBirth).toISOString().split('T')[0] : ''
        };
        setSelectedEmployee(formattedEmployee);
        setIsEditModalOpen(true);
    };

    const closeEditModal = () => {
        setIsEditModalOpen(false);
        setSelectedEmployee(null);
        setFormError('');
    };

    const handleEditChange = (e) => {
        const { name, value } = e.target;
        setSelectedEmployee(prev => ({ ...prev, [name]: value }));
    };

    const handleEditSubmit = (e) => {
        e.preventDefault();
        setFormError('');
        if (!selectedEmployee) return;

        // Convert age and experience back to numbers before sending
        const payload = { ...selectedEmployee, age: parseInt(selectedEmployee.age), totalExperience: parseInt(selectedEmployee.totalExperience) };

        updateEmployee(selectedEmployee.id, payload)
            .then(() => {
                closeEditModal();
                fetchEmployees(); // Refresh the table data
            })
            .catch(err => setFormError(err.response?.data || 'Failed to update employee.'));
    };

    if (isLoading) return <p>Loading employees...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div>
            <h2>Employee Management</h2>
            {/* Filters would go here */}

            <table className="app-table">
                <thead>
                    <tr><th>Name</th><th>Email</th><th>Department</th><th>Status</th><th>Actions</th></tr>
                </thead>
                <tbody>
                    {employees.map(emp => (
                        <tr key={emp.id}>
                            <td>{emp.name}</td>
                            <td>{emp.email}</td>
                            <td>{emp.department || 'N/A'}</td>
                            <td>{emp.status || 'N/A'}</td>
                            <td>
                                {/* REMOVED "View" button, updated "Edit" button */}
                                <button
                                    onClick={() => openEditModal(emp)}
                                    className="action-button"
                                    style={{ backgroundColor: '#ffc107', color: 'black' }}
                                >
                                    Edit
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* --- Edit Employee Modal --- */}
            {selectedEmployee && (
                <Modal isOpen={isEditModalOpen} onRequestClose={closeEditModal} className="modal-content" overlayClassName="modal-overlay">
                    <h2>Edit Employee: {selectedEmployee.name}</h2>
                    <button onClick={closeEditModal} className="modal-close-button">Ã—</button>
                    {formError && <p className="error-message">{formError}</p>}
                    <form onSubmit={handleEditSubmit}>
                        {/* Reusing the same form structure as the onboarding page */}
                        <div className="form-group"><label>Name</label><input type="text" name="name" value={selectedEmployee.name} onChange={handleEditChange} required /></div>
                        <div className="form-group"><label>Email</label><input type="email" name="email" value={selectedEmployee.email} onChange={handleEditChange} required /></div>
                        <div className="form-group"><label>Department</label><input type="text" name="department" value={selectedEmployee.department} onChange={handleEditChange} /></div>
                        <div className="form-group"><label>Status</label><select name="status" value={selectedEmployee.status} onChange={handleEditChange}><option value="ON_PROBATION">On Probation</option><option value="ACTIVE">Active</option><option value="INACTIVE">Inactive</option></select></div>
                        <div className="form-group"><label>Date Joined</label><input type="date" name="dateJoined" value={selectedEmployee.dateJoined} onChange={handleEditChange} /></div>
                        {/* You can add all other fields here (age, experience, etc.) */}
                        <button type="submit" className="btn">Save Changes</button>
                    </form>
                </Modal>
            )}
        </div>
    );
};

export default EmployeeListPage;
