import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
// ... other imports
import { getPendingLeaveRequests, updateLeaveRequestStatus } from '../services/api';
import '../components/Table.css';

const LeaveApprovalPage = () => {
    const [pendingRequests, setPendingRequests] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchPendingRequests = () => {
        setIsLoading(true);
        setError(null);
        getPendingLeaveRequests()
            .then(res => {
                setPendingRequests(res.data);
            })
            .catch(err => {
                console.error("!!! CRITICAL API ERROR !!!", err);
                let detailedError = "An unknown error occurred.";
                if (err.response) {
                    detailedError = `Error: ${err.response.status}. Message: ${JSON.stringify(err.response.data)}`;
                } else if (err.request) {
                    detailedError = "The server is not responding. Please ensure the backend is running.";
                } else {
                    detailedError = `Request setup error: ${err.message}`;
                }
                setError(detailedError);
            })
            .finally(() => {
                setIsLoading(false);
            });
    };

    useEffect(() => { fetchPendingRequests(); }, []);

    // ... handleAction is correct ...

    if (isLoading) return <p>Loading pending requests...</p>;

    if (error) {
        return (
            <div>
                <h2>Approve Leave Requests</h2>
                <div className="error-message">
                    <h4>Failed to Load Data</h4>
                    <pre>{error}</pre>
                    <button className="btn btn-primary btn-sm" onClick={fetchPendingRequests}>Try Again</button>
                </div>
            </div>
        );
    }

    return (
        <div>
            <h2>Approve Leave Requests</h2>
            {/* ... table content is correct, using req.employeeName etc. ... */}
        </div>
    );
};
export default LeaveApprovalPage;
