import React, { useState, useEffect } from 'react';
import { getAdminDashboardStats } from '../services/api';
import Card from '../components/Card';
import ClockWidget from '../components/ClockWidget';

const AdminHomePage = () => {
    const [stats, setStats] = useState(null);
    const [error, setError] = useState('');

    // Get user details from localStorage for a personalized greeting
    const adminUser = JSON.parse(localStorage.getItem('user'));
    const adminName = adminUser?.username || 'Admin';

    // Fetch the dashboard statistics when the component mounts
    useEffect(() => {
        getAdminDashboardStats()
            .then(response => {
                setStats(response.data);
            })
            .catch(err => {
                setError('Failed to load dashboard statistics.');
                console.error(err);
            });
    }, []); // The empty dependency array ensures this runs only once

    return (
        <div>
            {/* --- 1. Page Header Section --- */}
            <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                marginBottom: '2rem'
            }}>
                <div>
                    {/* Updated with "PayFlow" branding */}
                    <h1 style={{ margin: 0 }}>PayFlow Admin Dashboard</h1>
                    <p style={{ margin: 0, color: '#6c757d', fontSize: '1.1rem' }}>
                        Welcome, {adminName}! Here is your system summary.
                    </p>
                </div>
                <ClockWidget />
            </div>

            {/* --- 2. System Statistics Card --- */}
            <div className="form-container">
                <h2 style={{marginTop: 0, borderBottom: '1px solid #eee', paddingBottom: '1rem'}}>System Statistics</h2>

                {error && <p className="error-message">{error}</p>}
                {!stats && !error && <p>Loading statistics...</p>}

                {stats && (
                    <div style={{
                        display: 'flex',
                        gap: '1.5rem',
                        flexWrap: 'wrap',
                        marginTop: '1.5rem'
                    }}>
                        <Card title="Total Active Users" value={stats.totalActiveUsers} />
                        <Card title="Disabled Users" value={stats.disabledUsers} />
                        <Card title="Total HRs" value={stats.totalHrs} />
                        <Card title="Total Managers" value={stats.totalManagers} />
                    </div>
                )}
            </div>
            
            {/* --- 3. Notes & Tips Card --- */}
            <div className="form-container" style={{marginTop: '2rem'}}>
                {/* Updated with "PayFlow" branding */}
                <h2 style={{marginTop: 0}}>About the PayFlow Admin Role</h2>
                <p>From the <strong>Manage Users</strong> page, you can create, enable, and disable accounts for all HR and Manager staff.</p>
                <p>Remember to use strong, temporary passwords when creating new users and advise them to reset their password upon their first login for security.</p>
            </div>
        </div>
    );
};

export default AdminHomePage;
