import React from 'react';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

const MySwal = withReactContent(Swal);

const TopNavBar = ({ user, onLogout }) => {
    // This function will now handle the confirmation logic
    const confirmLogout = () => {
        MySwal.fire({
            title: 'Are you sure?',
            text: "You will be logged out of your session.",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, log out!',
            cancelButtonText: 'No, stay logged in',
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            customClass: {
                confirmButton: 'btn btn-primary',
                cancelButton: 'btn btn-secondary ml-2'
            },
            buttonsStyling: false // Use Bootstrap classes for buttons
        }).then((result) => {
            // If the user clicks "Yes, log out!"
            if (result.isConfirmed) {
                // Call the actual onLogout function passed down from App.js
                onLogout();
            }
        });
    };

    return (
        <div className="top-navbar">
            <span>Welcome, <strong>{user?.username || 'User'}</strong></span>
            {/* The button now calls our new confirmation function */}
            <button onClick={confirmLogout} className="btn btn-danger btn-sm ml-3">
                Logout
            </button>
        </div>
    );
};

export default TopNavBar;
