import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { getManageableUsers, updateUserStatus, createUser } from '../services/api';
import '../components/Modal.css';
import '../components/Table.css'; // <-- 1. IMPORT THE NEW TABLE CSS

Modal.setAppElement('#root');

const ManageUsersPage = () => {
    // ... all your existing state and functions are correct ...
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    const fetchUsers = () => { /* ... */ };
    useEffect(() => { fetchUsers(); }, []);
    const handleStatusToggle = (userId, currentStatus) => { /* ... */ };
    
    // ... modal logic is also correct ...

    if (isLoading) return <p>Loading users...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h2>Manage Users (HR & Managers)</h2>
                {/* ... Create User Button ... */}
            </div>

            {/* 2. ADD THE 'app-table' CLASSNAME TO THE TABLE */}
            <table className="app-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.username}</td>
                            <td>{user.email || 'N/A'}</td>
                            <td>{user.role.replace('ROLE_', '')}</td>
                            <td>
                                {/* Optional: Add styling for the status text */}
                                <span style={{
                                    color: user.enabled ? '#28a745' : '#dc3545',
                                    fontWeight: 'bold'
                                }}>
                                    {user.enabled ? 'Active' : 'Disabled'}
                                </span>
                            </td>
                            <td>
                                {/* 3. USE THE NEW ACTION BUTTON CLASSES */}
                                <button
                                    onClick={() => handleStatusToggle(user.id, user.enabled)}
                                    className={`action-button ${user.enabled ? 'btn-disable' : 'btn-enable'}`}
                                >
                                    {user.enabled ? 'Disable' : 'Enable'}
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* ... Your Modal component ... */}
        </div>
    );
};

export default ManageUsersPage;
