import React, { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import { getPendingLeaveRequests, updateLeaveRequestStatus } from '../services/api';
import '../components/Table.css';

const MySwal = withReactContent(Swal);

const LeaveApprovalPage = () => {
    const [pendingRequests, setPendingRequests] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    const fetchPendingRequests = () => { /* ... same as previous ... */ };
    useEffect(() => { fetchPendingRequests(); }, []);
    const handleAction = (requestId, newStatus) => { /* ... same as previous ... */ };
    
    if (isLoading) return <p>Loading pending requests...</p>;

    return (
        <div>
            <h2>Approve Leave Requests</h2>
            <p>Review and act upon pending leave requests from all employees.</p>
            <table className="app-table">
                <thead><tr><th>Employee Name</th><th>Leave Type</th><th>Dates</th><th>Reason</th><th>Actions</th></tr></thead>
                <tbody>
                    {pendingRequests.length > 0 ? pendingRequests.map(req => (
                        <tr key={req.id}>
                            <td>{req.employee.name}</td>
                            <td>{req.leaveType.name}</td>
                            <td>{`${new Date(req.startDate).toLocaleDateString()} to ${new Date(req.endDate).toLocaleDateString()}`}</td>
                            <td>{req.reason}</td>
                            <td>
                                <button className="btn btn-success btn-sm" onClick={() => handleAction(req.id, 'APPROVED')}>Approve</button>
                                <button className="btn btn-danger btn-sm ml-2" onClick={() => handleAction(req.id, 'DENIED')}>Deny</button>
                            </td>
                        </tr>
                    )) : <tr><td colSpan="5">There are no pending leave requests.</td></tr>}
                </tbody>
            </table>
        </div>
    );
};
export default LeaveApprovalPage;
