import axios from 'axios';

// Create an instance of axios
const api = axios.create({
    baseURL: 'http://localhost:8080/api', // Your backend API base URL
    headers: {
        'Content-Type': 'application/json',
    },
});

// Add a request interceptor to include the token in headers for every request
api.interceptors.request.use(
    (config) => {
        const user = JSON.parse(localStorage.getItem('user'));
        if (user && user.token) {
            // Prepend "Bearer " to the token
            config.headers['Authorization'] = 'Bearer ' + user.token;
        }
        return config;
    },
    (error) => {
        return Promise.reject(error);
    }
);


// --- ORIGINAL AUTH/ADMIN FUNCTIONS (Week 1) ---

export const login = (username, password) => {
    return api.post('/auth/login', { username, password });
};

export const resetPassword = (newPassword) => {
    return api.post('/auth/reset-password', { newPassword });
};

export const createUser = (username, password, role) => {
    return api.post('/admin/users', { username, password, role });
};


// --- NEW HR DASHBOARD FUNCTIONS (Week 2) ---

export const getDashboardStats = () => {
    return api.get('/hr/dashboard/stats');
};

export const getEmployees = (params) => {
    // params will be an object like { page: 0, size: 10, department: 'Engineering' }
    return api.get('/hr/employees', { params });
};

// ... after the HR functions ...

// --- ADMIN DASHBOARD SERVICE ---
export const getAdminDashboardStats = () => {
    return api.get('/admin/dashboard/stats');
};

export const getManageableUsers = () => {
    return api.get('/admin/users');
};

export const updateUserStatus = (userId, isEnabled) => {
    return api.put(`/admin/users/${userId}/status`, { enabled: isEnabled });
};
// --- This is the default export ---
export default api;
