import React, { useState } from 'react';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import { onboardEmployee } from '../services/api';

const MySwal = withReactContent(Swal);

const OnboardingPage = () => {
    // The initial state MUST include the 'dateJoined' property.
    const getInitialState = () => ({
        name: '', email: '', department: 'Engineering', age: '',
        totalExperience: '', pastExperience: '',
        dateJoined: new Date().toISOString().split('T')[0], // Default to today
        dateOfBirth: '', status: 'ON_PROBATION'
    });

    const [formData, setFormData] = useState(getInitialState());

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // The 'payload' object MUST contain the 'dateJoined' from the state.
            const payload = { ...formData, age: parseInt(formData.age), totalExperience: parseInt(formData.totalExperience) };
            await onboardEmployee(payload);
            
            MySwal.fire({ icon: 'success', title: 'Onboarding Complete!', text: `Employee '${formData.name}' has been successfully onboarded.` });
            setFormData(getInitialState());

        } catch (err) {
            MySwal.fire({ icon: 'error', title: 'Onboarding Failed', text: err.response?.data || 'Please check all fields and try again.' });
        }
    };

    return (
        <div>
            <h2>Onboard New Employee</h2>
            <div className="form-container" style={{ maxWidth: '800px', margin: '2rem auto' }}>
                <form onSubmit={handleSubmit}>
                    <div className="row">
                        <div className="col-md-6">
                            {/* Form fields for name, email, etc. */}
                        </div>
                        <div className="col-md-6">
                            <div className="form-group">
                                <label>Date Joined</label>
                                {/* The 'name' attribute MUST be "dateJoined" to match the state. */}
                                <input type="date" className="form-control" name="dateJoined" value={formData.dateJoined} onChange={handleChange} required />
                            </div>
                            {/* Other form fields */}
                        </div>
                    </div>
                    {/* ...rest of the form... */}
                    <button type="submit" className="btn btn-primary btn-block">Complete Onboarding</button>
                </form>
            </div>
        </div>
    );
};

export default OnboardingPage;
