import React, { useState, useEffect } from 'react';
import { getDashboardStats } from '../services/api';
import Card from '../components/Card';
import ClockWidget from '../components/ClockWidget';

const DashboardHomePage = () => {
    const [stats, setStats] = useState(null);
    const [error, setError] = useState('');

    // Get user from localStorage (adjust this if using Context or Redux)
    const user = JSON.parse(localStorage.getItem('user'));
    const username = user?.username || 'User';

    const currentDate = new Date().toLocaleDateString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric'
    });

    useEffect(() => {
        getDashboardStats()
            .then(response => {
                setStats(response.data);
            })
            .catch(err => {
                setError('Failed to load dashboard data.');
                console.error(err);
            });
    }, []);

    if (error) return <p className="error-message">{error}</p>;
    if (!stats) return <p>Loading dashboard...</p>;

    return (
        <div>
            {/* --- Welcome Header Section --- */}
            <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                marginBottom: '2rem'
            }}>
                <div>
                    <h1 style={{ margin: 0 }}>Welcome, {username}!</h1>
                    <p style={{ margin: 0, color: '#6c757d', fontSize: '1.1rem' }}>
                        Here is your system summary for today, {currentDate}.
                    </p>
                </div>
                <ClockWidget />
            </div>

            {/* --- Stats Cards --- */}
            <div style={{ display: 'flex', gap: '1.5rem', flexWrap: 'wrap' }}>
                <Card title="Total Employees" value={stats.totalEmployees} />
                <Card title="New This Month" value={stats.newThisMonth} />
                <Card title="Pending Profiles" value={stats.pendingProfiles} />
            </div>
        </div>
    );
};

export default DashboardHomePage;
