import React, { useState, useEffect } from 'react';
import { Navigate, Route, Routes, useNavigate } from 'react-router-dom';

// Import all required components and pages
import LoginPage from './pages/LoginPage';
import ResetPasswordPage from './pages/ResetPasswordPage';

// Import Layouts
import AdminLayout from './layouts/AdminLayout';
import DashboardLayout from './layouts/DashboardLayout';

// Import Admin Pages
import AdminHomePage from './pages/AdminHomePage';
import ManageUsersPage from './pages/ManageUsersPage';

// Import HR Pages
import DashboardHomePage from './pages/DashboardHomePage';
import EmployeeListPage from './pages/EmployeeListPage';

// Import global styles
import './App.css';

/**
 * A wrapper component to protect routes that require authentication and role-based access.
 * @param {object} props
 * @param {React.ReactNode} props.children - The component to render if authorized.
 * @param {string[]} props.allowedRoles - An array of roles allowed to access this route (e.g., ['ROLE_ADMIN']).
 */
const ProtectedRoute = ({ children, allowedRoles }) => {
    const user = JSON.parse(localStorage.getItem('user'));

    // 1. Check if user is logged in
    if (!user) {
        // If not logged in, redirect to the login page
        return <Navigate to="/login" />;
    }

    // 2. Check if the user's role is permitted
    const hasRole = user.roles.some(role => allowedRoles.includes(role));
    if (!hasRole) {
        // If the user has the wrong role, redirect them to the login page as a fallback.
        // In a more complex app, you might redirect to an "Unauthorized" page.
        return <Navigate to="/login" />;
    }

    // 3. Check for mandatory password reset
    if (user.firstTimeLogin) {
        // If it's a first-time login, force redirect to the reset password page.
        // This check is crucial for security.
        return <Navigate to="/reset-password" />;
    }

    // 4. If all checks pass, render the requested component
    return children;
};


function App() {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    // On initial application load, check localStorage for a persisted user session.
    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    /**
     * Handles successful login by saving user data to state and localStorage,
     * then navigating to the appropriate dashboard based on role.
     * @param {object} userData - The user data received from the API.
     */
    const handleLoginSuccess = (userData) => {
        localStorage.setItem('user', JSON.stringify(userData));
        setUser(userData);

        // First, check if a password reset is required.
        if (userData.firstTimeLogin) {
            navigate('/reset-password');
            return;
        }

        // Navigate to the correct dashboard based on the user's role.
        if (userData.roles.includes('ROLE_ADMIN')) {
            navigate('/admin/dashboard');
        } else if (userData.roles.includes('ROLE_HR')) {
            navigate('/hr/dashboard');
        } else if (userData.roles.includes('ROLE_MANAGER')) {
            // Future-proofing for the Manager role
            navigate('/manager/dashboard'); 
        } else {
            // Fallback for unknown roles
            navigate('/login');
        }
    };

    /**
     * Handles user logout by clearing user data and redirecting to the login page.
     */
    const handleLogout = () => {
        localStorage.removeItem('user');
        setUser(null);
        navigate('/login');
    };

    return (
        <Routes>
            {/* PUBLIC ROUTES (Accessible to everyone) */}
            <Route path="/login" element={<LoginPage onLoginSuccess={handleLoginSuccess} />} />
            <Route path="/reset-password" element={<ResetPasswordPage onPasswordResetSuccess={handleLogout} />} />

            {/* ADMIN DASHBOARD ROUTES */}
            <Route
                path="/admin/*" // The "/*" allows for nested routes
                element={
                    <ProtectedRoute allowedRoles={['ROLE_ADMIN']}>
                        <AdminLayout />
                    </ProtectedRoute>
                }
            >
                <Route path="dashboard" element={<AdminHomePage />} />
                <Route path="manage-users" element={<ManageUsersPage />} />
                {/* Redirects /admin to /admin/dashboard by default */}
                <Route index element={<Navigate to="dashboard" />} />
            </Route>

            {/* HR DASHBOARD ROUTES */}
            <Route
                path="/hr/*" // The "/*" allows for nested routes
                element={
                    <ProtectedRoute allowedRoles={['ROLE_HR']}>
                        <DashboardLayout />
                    </ProtectedRoute>
                }
            >
                <Route path="dashboard" element={<DashboardHomePage />} />
                <Route path="employees" element={<EmployeeListPage />} />
                {/* Redirects /hr to /hr/dashboard by default */}
                <Route index element={<Navigate to="dashboard" />} />
            </Route>

            {/* DEFAULT CATCH-ALL ROUTE */}
            {/* If a user lands on any other URL, redirect them to the login page. */}
            <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
    );
}

export default App;
