import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import { getEmployees, updateEmployee, deleteEmployee } from '../services/api';
import '../components/Table.css';
import '../components/Modal.css';

const MySwal = withReactContent(Swal);
Modal.setAppElement('#root');

const EmployeeListPage = () => {
    const [employees, setEmployees] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [selectedEmployee, setSelectedEmployee] = useState(null);

    const fetchEmployees = () => {
        setIsLoading(true);
        getEmployees({ page: 0, size: 20 })
            .then(response => setEmployees(response.data.content))
            .catch(err => setError('Failed to load employees.'))
            .finally(() => setIsLoading(false));
    };

    useEffect(() => { fetchEmployees(); }, []);

    const openEditModal = (employee) => {
        const formattedEmployee = {
            ...employee,
            dateJoined: employee.dateJoined ? new Date(employee.dateJoined).toISOString().split('T')[0] : '',
            dateOfBirth: employee.dateOfBirth ? new Date(employee.dateOfBirth).toISOString().split('T')[0] : ''
        };
        setSelectedEmployee(formattedEmployee);
        setIsEditModalOpen(true);
    };

    const closeEditModal = () => {
        setIsEditModalOpen(false);
        setSelectedEmployee(null);
    };

    const handleEditChange = (e) => {
        const { name, value } = e.target;
        setSelectedEmployee(prev => ({ ...prev, [name]: value }));
    };

    const handleEditSubmit = (e) => {
        e.preventDefault();
        if (!selectedEmployee) return;
        const payload = { ...selectedEmployee, age: parseInt(selectedEmployee.age), totalExperience: parseInt(selectedEmployee.totalExperience) };
        updateEmployee(selectedEmployee.id, payload)
            .then(() => {
                closeEditModal();
                MySwal.fire('Updated!', 'Employee details have been saved.', 'success');
                fetchEmployees();
            })
            .catch(err => MySwal.fire('Error!', 'Failed to update employee details.', 'error'));
    };

    /**
     * Handles the delete action. Shows a confirmation dialog before proceeding.
     * @param {object} employee The employee object to be deleted.
     */
    const handleDelete = (employee) => {
        MySwal.fire({
            title: 'Are you sure?',
            html: `You are about to permanently delete "<strong>${employee.name}</strong>".<br/>This action cannot be undone.`,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, delete it!',
            cancelButtonText: 'No, cancel',
            confirmButtonColor: '#d33',
            cancelButtonColor: '#3085d6',
            customClass: {
                confirmButton: 'btn btn-danger',
                cancelButton: 'btn btn-secondary ms-3'
            },
            buttonsStyling: false
        }).then((result) => {
            if (result.isConfirmed) {
                deleteEmployee(employee.id)
                    .then(() => {
                        MySwal.fire(
                            'Deleted!',
                            `Employee "${employee.name}" has been deleted.`,
                            'success'
                        );
                        fetchEmployees(); // Refresh the list after successful deletion
                    })
                    .catch(err => {
                        MySwal.fire(
                            'Error!',
                            'Failed to delete the employee.',
                            'error'
                        );
                    });
            }
        });
    };

    if (isLoading) return <p>Loading employees...</p>;
    if (error) return <p className="error-message">{error}</p>;

    return (
        <div>
            <h2>Employee Management</h2>
            <table className="app-table">
                <thead>
                    <tr><th>Name</th><th>Email</th><th>Department</th><th>Status</th><th>Actions</th></tr>
                </thead>
                <tbody>
                    {employees.map(emp => (
                        <tr key={emp.id}>
                            <td>{emp.name}</td>
                            <td>{emp.email}</td>
                            <td>{emp.department || 'N/A'}</td>
                            <td>{emp.status || 'N/A'}</td>
                            <td>
                                <button onClick={() => openEditModal(emp)} className="btn btn-warning btn-sm">Edit</button>
                                <button onClick={() => handleDelete(emp)} className="btn btn-danger btn-sm ms-3">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {selectedEmployee && (
                <Modal isOpen={isEditModalOpen} onRequestClose={closeEditModal} className="modal-content" overlayClassName="modal-overlay">
                    <h2>Edit Employee: {selectedEmployee.name}</h2>
                    <button onClick={closeEditModal} className="modal-close-button">×</button>
                    <form onSubmit={handleEditSubmit} className="mt-3">
                        <div className="row">
                            <div className="col-md-6">
                                <div className="form-group"><label>Name</label><input type="text" className="form-control" name="name" value={selectedEmployee.name} onChange={handleEditChange} required /></div>
                                <div className="form-group"><label>Email</label><input type="email" className="form-control" name="email" value={selectedEmployee.email} onChange={handleEditChange} required /></div>
                            </div>
                            <div className="col-md-6">
                                <div className="form-group"><label>Department</label><input type="text" className="form-control" name="department" value={selectedEmployee.department || ''} onChange={handleEditChange} /></div>
                                <div className="form-group"><label>Status</label><select className="form-control" name="status" value={selectedEmployee.status || 'ACTIVE'} onChange={handleEditChange}><option value="ON_PROBATION">On Probation</option><option value="ACTIVE">Active</option><option value="INACTIVE">Inactive</option></select></div>
                            </div>
                             <div className="col-md-6">
                                <div className="form-group"><label>Age</label><input type="number" className="form-control" name="age" value={selectedEmployee.age} onChange={handleEditChange} required/></div>
                                <div className="form-group"><label>Total Experience (years)</label><input type="number" className="form-control" name="age" value={selectedEmployee.totalExperience} onChange={handleEditChange} required/></div>
                            </div>
                            <div className="col-md-6">
                                <div className="form-group"><label>Date Joined</label><input type="date" className="form-control" name="" value={selectedEmployee.dateJoined} onChange={handleEditChange} required/></div>
                               <div className="form-group"><label>Date of Birth</label><input type="date" className="form-control" name="" value={selectedEmployee.dateOfBirth} onChange={handleEditChange} required/></div>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary btn-block mt-4">Save Changes</button>
                    </form>
                </Modal>
            )}
        </div>
    );
};

export default EmployeeListPage;
